function create_color_mapping(array1, array2) {
  const mapping = {};
  array1.forEach((obj1, index) =&gt; {
    const obj2 = array2[index];
    const key = obj1.value; // Use obj1.value as the key

    if (obj1.value === obj2.value) {
      mapping[key] = "#5f9c5f";
    } else {
      mapping[key] = "#fc4747";
    }
  });
  return mapping
}

